--
-- Name: aggregate(character varying, character varying, character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION aggregate(_schema character varying, _entity character varying, _attribute character varying, _function character varying, _filter character varying, _where character varying) RETURNS numeric
    LANGUAGE plpgsql STABLE STRICT
    AS $$
declare
  rv numeric;
begin
  -- RAISE NOTICE 'agg 1-% 2-% 3-% 4-% 5-% 6-%', _schema,_entity,_attribute,_function,_filter,_where;

  /* RAISE NOTICE 'agg_type 1-% 2-% 3-% 4-% 5-% 6-%',
    pg_typeof( _schema),
    pg_typeof(_entity),
    pg_typeof(_attribute),
    pg_typeof(_function),
    pg_typeof(_filter),
    pg_typeof(_where);
  */
  execute
    'select ' || quote_ident(_function) || '(' || (
      case _attribute
        when '*' then '*' else (
          case
            when (length(_filter) > 0) then
              (/* const */ _filter || '(' || quote_ident(_attribute) || ')')
            else
              quote_ident(_attribute)
          end
        )
      end
    ) || ') from ' || quote_ident(_schema) || '.' || quote_ident(_entity) || (
      case
        when (length(_where) > 0) then (' where ' || /* const */ _where)
        else ''
      end
    ) || ';'
  into rv;

  return rv;
end
$$;


--
-- Name: append_meta_attribute_statistics(character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION append_meta_attribute_statistics(_schema character varying, _entity character varying, _subset_attribute character varying, _subset_id character varying) RETURNS boolean
    LANGUAGE plpgsql STRICT
    AS $_$
begin
  -- raise NOTICE 'amas %, %, %: %',
  --   _schema, _entity, _subset_attribute, _subset_id;

  insert into
    target.meta_attribute_statistics
  select
    nextval('target.seq_meta_attribute_statistics_serial'),
    $4 as subset_id, s.*,
    transform.calculate_ci(
      s.mean, s.stddev, s.n, -1, 0.05 /* 95% */
    ) as ci_lower,
    transform.calculate_ci(
      s.mean, s.stddev, s.n, 1, 0.05 /* 95% */
    ) as ci_upper,
    transform.calculate_ci(
      s.subset_mean, s.subset_stddev, s.subset_n, -1, 0.05 /* 95% */
    ) as subset_ci_lower,
    transform.calculate_ci(
      s.subset_mean, s.subset_stddev, s.subset_n, 1, 0.05 /* 95% */
    ) as subset_ci_upper
  from
    transform.entity_subset_statistics(
      $1, $2, '', quote_ident($3) || ' = ' || quote_literal($4)
    ) as s;

  return true;
end
$_$;


--
-- Name: append_meta_attribute_values(character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION append_meta_attribute_values(_subset_attribute character varying, _subset_id character varying) RETURNS boolean
    LANGUAGE sql STRICT
    AS $_$
  insert into
    target.meta_attribute_values
  select
    nextval('target.seq_meta_attribute_values_serial'),
    (select s.meta_attribute_statistic_id
      from target.meta_attribute_statistics s
      where s.subset_id = $2
        and s.entity_schema = v.entity_schema
        and s.entity_name = v.entity_name
        and s.attribute_name = v.attribute_name),
    $2 as subset_id, v.*,
    transform.aggregate(
      v.entity_schema, v.entity_name, v.attribute_name, 'count', '',
        (quote_ident(v.attribute_name) || ' = ' || quote_literal(v.value))
    ) as count,
    transform.aggregate(
      v.entity_schema, v.entity_name, v.attribute_name, 'count', '',
        (quote_ident(v.attribute_name) || ' = ' || quote_literal(v.value)
          || ' and ' || quote_ident($1) || ' = ' || quote_literal($2))
    ) as subset_count
  from
    source.meta_attribute_values v;
  select true;
$_$;


--
-- Name: calculate_ci(numeric, numeric, numeric, numeric, numeric); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION calculate_ci(_mean numeric, _stddev numeric, _n numeric, _sign numeric, _alpha numeric) RETURNS numeric
    LANGUAGE plpgsql STRICT
    AS $$
begin
  return (
    _mean + (
      _sign * (
        select t from transform.critical_values
          where alpha = _alpha and df < _n order by df desc limit 1
      ) * (_stddev / sqrt(_n))
    )
  );
end
$$;


--
-- Name: column_comment(character varying, character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION column_comment(_schema character varying, _entity character varying, _attribute character varying) RETURNS character varying
    LANGUAGE sql STABLE STRICT
    AS $_$
  select col_description(
    (
      select
        c.oid
      from
        pg_class c
          left join pg_namespace ns on c.relnamespace = ns.oid
      where
        ns.nspname = $1 and c.relname = $2
    ),
    (
      select
        c.ordinal_position
      from
        information_schema.columns c
      where
        c.table_schema = $1
        and c.table_name = $2 and c.column_name = $3
    )
  );
$_$;


--
-- Name: entity_statistics(character varying, character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION entity_statistics(_schema character varying, _entity character varying, _where character varying) RETURNS SETOF entity_statistics_type
    LANGUAGE sql STABLE STRICT
    AS $_$
  select
    $1 as entity_schema,
    $2 as entity_name,
    cast(ordinal_position as bigint) as attribute_index,
    column_name as attribute_name,
    transform.column_comment($1, $2, column_name) as attribute_description,
    data_type as attribute_data_type,
    transform.aggregate($1, $2, '*', 'count', '', $3) as count,
    transform.aggregate(
      $1, $2, column_name::TEXT, 'count', 'transform.nullify', $3 /*a*/
    ) as n,
    cast(
      (100.0 *
        transform.aggregate(
          $1, $2, column_name::TEXT, 'count', 'transform.nullify', $3 /*a1*/ 
        )
      )
      / transform.aggregate(
        $1, $2, '*', 'count', '', $3/*a*/ 
      ) as numeric(63, 2)
    ) as n_percent,
    (
      case
        when transform.is_numeric_type(data_type) then
          cast(
            transform.aggregate(
              $1, $2, column_name::TEXT, 'avg', 'transform.nullify', $3/*a2*/ 
            ) as numeric(63, 2)
          )
        else
          null
      end
    ) as mean,
    (
      case
        when transform.is_numeric_type(data_type) then
          cast(
            transform.aggregate(
              $1, $2, column_name::TEXT, 'stddev_samp', 'transform.nullify', $3 /*a3*/ 
            ) as numeric(63, 2)
          )
        else
          null
      end
    ) as stddev,
    (
      case
        when transform.is_numeric_type(data_type) then
          cast(
            transform.aggregate(
              $1, $2, column_name::TEXT, 'min', 'transform.nullify', $3 /*a4*/ 
            ) as numeric(63, 2)
          )
        else
          null
      end
    ) as min,
   (
      case
        when transform.is_numeric_type(data_type) then
          cast(
            transform.aggregate($1, $2, column_name::TEXT, 'max', 'transform.nullify', $3) /*a5*/ 
              as numeric(63, 2)
          )
        else
          null
      end
    ) as max
    from information_schema.columns
  where
    table_schema::TEXT = $1 and table_name::TEXT = $2;
$_$;


--
-- Name: entity_subset_statistics(character varying, character varying, character varying, character varying); Type: FUNCTION; Schema: transform; Owner: -
--

CREATE FUNCTION entity_subset_statistics(_schema character varying, _entity character varying, _where1 character varying, _where2 character varying) RETURNS SETOF entity_subset_statistics_type
    LANGUAGE sql STABLE STRICT
    AS $_$
  select
    superset.entity_schema, superset.entity_name, superset.attribute_index,
    superset.attribute_name, superset.attribute_description,
    superset.attribute_data_type, superset.count, superset.n,
    superset.n_percent, superset.mean, superset.stddev, superset.min,
    superset.max, subset.count as subset_count, subset.n as subset_n,
    subset.n_percent as subset_n_percent, subset.mean as subset_mean,
    subset.stddev as subset_stddev, subset.min as subset_min,
    subset.max as subset_max
  from
    transform.entity_statistics($1, $2, $3) as superset
    inner join
      transform.entity_statistics($1, $2, $4) as subset
        on superset.entity_schema = subset.entity_schema
          and superset.entity_name = subset.entity_name
          and superset.attribute_name = subset.attribute_name
  order by
    superset.entity_schema, superset.entity_name, superset.attribute_index;
$_$;


